mapper.update:
UPDATE room_join_request
SET status = '승인'
WHERE join_id = #{joinId}
  AND host_user_email = #{hostUserEmail}

UPDATE room_join_request
SET status = '거절'
WHERE join_id = #{joinId}
  AND host_user_email = #{hostUserEmail}

mapper.select:
SELECT COUNT(*)
FROM room_join_request
WHERE room_id = #{roomId}
  AND request_user_email = #{requestUserEmail}
  AND status IN ('신청중', '승인')

SELECT room_id
FROM room_join_request
WHERE join_id = #{joinId}

SELECT max_participants
FROM room
WHERE room_id = #{roomId}

mapper.update:
UPDATE room
SET status = 'CLOSED'
WHERE room_id = #{roomId}

mapper.select:
SELECT gender
FROM users
WHERE email = #{email}

SELECT gender
FROM room
WHERE room_id = #{roomId}

SELECT status
FROM room_join_request
WHERE room_id = #{roomId}
  AND request_user_email = #{requestUserEmail}
