mapper.select:
SELECT p.post_id, p.room_id, p.user_id, COALESCE(NULLIF(u.nickname, ''), u.name) AS nickname, p.category, p.title, p.content, p.is_pinned, p.view_count, p.created_at, p.updated_at, p.deleted_at, (SELECT COUNT(*) FROM board_comments c WHERE c.post_id = p.post_id AND c.deleted_at IS NULL) AS comment_count, (SELECT COUNT(*) FROM board_post_attachments a WHERE a.post_id = p.post_id) AS attachment_count
FROM board_posts p LEFT JOIN users u ON u.user_id = p.user_id WHERE p.deleted_at IS NULL AND p.room_id = #{roomId}
AND p.category = #{category}
AND ( p.title LIKE CONCAT('%', #{keyword}, '%') OR p.content LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY p.is_pinned DESC, p.post_id DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM board_posts
WHERE deleted_at IS NULL
AND room_id = #{roomId}

    AND category = #{category}


    AND (
    title LIKE CONCAT('%', #{keyword}, '%')
    OR content LIKE CONCAT('%', #{keyword}, '%')
    )

mapper.update:
UPDATE board_posts
SET category = #{category},
    title    = #{title},
    content  = #{content},
    is_pinned = #{isPinned}
WHERE post_id = #{postId}
  AND user_id = #{userId}
  AND deleted_at IS NULL

UPDATE board_posts
SET deleted_at = NOW()
WHERE post_id = #{postId}
  AND user_id = #{userId}
  AND deleted_at IS NULL

UPDATE board_posts
SET is_pinned = #{isPinned}
WHERE post_id = #{postId}
  AND user_id = #{userId}
  AND deleted_at IS NULL

UPDATE board_posts
SET is_pinned = #{isPinned}
WHERE post_id = #{postId}
  AND deleted_at IS NULL
