mapper.select:
SELECT
post_id, room_id, user_id, category, title, content,
is_pinned, view_count, created_at, updated_at, deleted_at
FROM board_posts
WHERE deleted_at IS NULL
AND room_id = #{roomId}

    AND category = #{category}


    AND (
    title LIKE CONCAT('%', #{keyword}, '%')
    OR content LIKE CONCAT('%', #{keyword}, '%')
    )

ORDER BY is_pinned DESC, post_id DESC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM board_posts
WHERE deleted_at IS NULL
AND room_id = #{roomId}

    AND category = #{category}


    AND (
    title LIKE CONCAT('%', #{keyword}, '%')
    OR content LIKE CONCAT('%', #{keyword}, '%')
    )

mapper.insert:
INSERT INTO board_posts (
room_id, user_id, category, title, content, is_pinned, view_count
) VALUES (
#{roomId}, #{userId}, #{category}, #{title}, #{content},

    #{isPinned}
    0
,
0
)

mapper.update:
UPDATE board_posts
SET
    category = #{category},
    title    = #{title},
    content  = #{content}
WHERE post_id = #{postId}
  AND user_id = #{userId}
  AND deleted_at IS NULL

UPDATE board_posts
SET deleted_at = NOW()
WHERE post_id = #{postId}
  AND user_id = #{userId}
  AND deleted_at IS NULL

UPDATE board_posts
SET is_pinned = #{isPinned}
WHERE post_id = #{postId}
  AND user_id = #{userId}
  AND deleted_at IS NULL
