) VALUES (
#{roomId}, #{userId}, #{title}, #{description},
#{startAt}, #{endAt}, #{startTime}, #{endTime}, #{type}, #{colorHex}, #{textColor}, #{customTypeLabel}
)

mapper.update:
UPDATE schedules
SET deleted_at = CURRENT_TIMESTAMP
WHERE schedule_id = #{scheduleId}
AND room_id = #{roomId}
AND deleted_at IS NULL
