mapper.insert:
INSERT INTO study_schedule (
round_num, study_date, description, room_id
) VALUES (
#{roundNum}, #{studyDate}, #{description}, #{roomId}
)

mapper.update:
UPDATE study_schedule
SET
round_num = #{roundNum},
study_date = #{studyDate},
description = #{description}
WHERE study_schedule_id = #{studyScheduleId}
AND room_id = #{roomId}

mapper.delete:
DELETE FROM study_schedule
WHERE study_schedule_id = #{studyScheduleId}
AND room_id = #{roomId}
