mapper.update:
UPDATE study_schedule
SET round_num = #{roundNum},
    study_date = #{studyDate},
    start_time = #{startTime},
    end_time = #{endTime},
    description = #{description}
WHERE study_schedule_id = #{studyScheduleId}
AND room_id = #{roomId}

mapper.delete:
DELETE FROM study_schedule
WHERE study_schedule_id = #{studyScheduleId}
AND room_id = #{roomId}
